// import InsuranceDetails from '../models/user';
import './ExpenseItems.css';

// A component (Custom) in React is Javascript function
// Can only have 1 root element per return statement/snippet
// Can use a div within a div

// Props - Passing data via properties

function ExpenseItem(props) {
    // const date = new Date();
    // const currentDate = date.getDay();
    // const expenseTitle = 'Car Insurance';
    // const expenseAmount = 294.85;
    console.log(props);
    return (
    <div className="expense-item"> 
        <div>
            <div>{props.date.toLocaleString('en-US', {month: 'long'})}</div>
            {/* <div>{props.date}</div>
            <div>{props.date}</div> */}
        </div>
        <div className="expense-item__description">
            <h2>{ props.title }</h2>
            <div className="expense-item__price">R{ props.amount }</div> 
        </div>
    </div>
    );
}

export default ExpenseItem;
